             <SearchBar onSearch={this.search} /> 
            <div className="App-playlist">
            <SearchResults 
            searchResults={this.state.SearchResults}
            onAdd={this.addTrack}
            /> 
            {/*pass this.state.searchResults into seacrchResults component*/}


            <Playlist playlistName={this.state.playlistName}
